namespace webAPI.Models.Elearning.configs
{
    public class ElearnigQueryConfig
    {
        public string Q_GET_COURE = "SELECT 'COURSE' DOC_TYPE, CM.COURSE_ID, CM.COURSE_DESC, CQ.QUERY_ID, PLACE, CQT.TIME_SEQ, LECT_NAME, APPLICANT_COUNT, COURSE_DATE QUERY_BEGIN, NVL ( COURSE_DATE + TO_NUMBER (DAY_HOUR) / 24 + TO_NUMBER (DAY_MIN) / 24 / 60, COURSE_DATE) QUERY_END, NVL (DAY_HOUR, 0) DAY_HOUR, NVL (DAY_MIN, 0) DAY_MIN, NVL (CQ.INSTANT_FLAG, 'F') INSTANT_FLAG, CQ.CR_USER_ID FROM KPDBA.COURSE_MASTER CM, KPDBA.COURSE_QUERY CQ, KPDBA.COURSE_QUERY_TIME CQT, (  SELECT QUERY_ID, LISTAGG (THAI_FNAME || ' ' || THAI_SNAME, ', ') WITHIN GROUP (ORDER BY QUERY_ID, THAI_FNAME, THAI_SNAME) LECT_NAME FROM KPDBA.COURSE_LECTURER CL, KPDBA.LECTURER_MASTER LM WHERE CL.LECT_ID = LM.LECT_ID GROUP BY QUERY_ID) LECT, (  SELECT QUERY_ID, COUNT (APP_USER_ID) APPLICANT_COUNT FROM KPDBA.COURSE_APPLICANT CA GROUP BY QUERY_ID) APPLI WHERE     CLOSE_FLAG = 'F' AND CM.COURSE_ID = CQ.COURSE_ID AND CQ.QUERY_ID = CQT.QUERY_ID(+) AND ONLINE_CLASS_FLAG = 'T' AND CQ.QUERY_ID = LECT.QUERY_ID(+) AND CQ.QUERY_ID = APPLI.QUERY_ID(+) AND NVL (INSTANT_FLAG, 'F') LIKE :as_instant_flag AND CQ.CR_USER_ID LIKE :as_user_id UNION ALL SELECT 'ISO' DOC_TYPE, IM.DOC_CODE || '/' || IM.DOC_REVISION DOC_CODE, DOC_NAME, ICQ.QUERY_ID, PLACE, ICT.TIME_SEQ, LECT_EMP_ID || ' ' || EMP_FNAME || ' ' || EMP_LNAME EMP_NAME, APPLICANT_COUNT, ICT.COURSE_DATE QUERY_BEGIN, NVL ( ICT.COURSE_DATE + TO_NUMBER (DAY_HOUR) / 24 + TO_NUMBER (DAY_MIN) / 24 / 60, ICT.COURSE_DATE) QUERY_END, NVL (DAY_HOUR, 0) DAY_HOUR, NVL (DAY_MIN, 0) DAY_MIN, NVL (ICQ.INSTANT_FLAG, 'F') INSTANT_FLAG, ICQ.CR_USER_ID FROM KPDBA.ISO_MASTER IM, KPDBA.ISO_COURSE_QUERY ICQ, KPDBA.ISO_COURSE_TIME ICT, KPDBA.EMPLOYEE EMP, (  SELECT QUERY_ID, COUNT (APP_EMP_ID) APPLICANT_COUNT FROM KPDBA.ISO_COURSE_APPLICANT CA GROUP BY QUERY_ID) APPLI WHERE     IM.DOC_CODE = ICQ.DOC_CODE AND IM.DOC_REVISION = ICQ.DOC_REVISION AND ICQ.QUERY_ID = ICT.QUERY_ID(+) AND DOC_STATUS <> 'C' AND ACCEPT_HIST_FLAG = 'T' AND EMP.EMP_ID = LECT_EMP_ID AND IM.ONLINE_CLASS_FLAG = 'T' AND ICQ.QUERY_ID = APPLI.QUERY_ID(+) AND NVL (INSTANT_FLAG, 'F') LIKE :as_instant_flag AND ICQ.CR_USER_ID LIKE :as_user_id";
        public string Q_GET_DEPARTMENT = "SELECT UNIT_ID, UNIT_DESC FROM (SELECT APPLICANT.DOC_TYPE, APPLICANT.QUERY_ID, APPLICANT.TIME_SEQ, APPLICANT.APP_EMP_ID, EMP_NAME, APPLICANT.UNIT_ID, UNT.UNIT_DESC, NVL (TRAINING_FLAG, 'F') TRAINING_FLAG FROM (SELECT 'ISO' DOC_TYPE, ICT.QUERY_ID, ICT.TIME_SEQ, EMP.UNIT_ID, CA.APP_EMP_ID, CA.APP_EMP_ID || ' ' || EMP_FNAME || ' ' || EMP_LNAME EMP_NAME FROM KPDBA.ISO_COURSE_APPLICANT CA, KPDBA.ISO_COURSE_TIME ICT, KPDBA.EMPLOYEE EMP WHERE     CA.APP_EMP_ID = EMP.EMP_ID AND CA.QUERY_ID = ICT.QUERY_ID AND CA.QUERY_ID = :as_query_id UNION ALL SELECT 'COURSE' DOC_TYPE, CA.QUERY_ID, CQT.TIME_SEQ, EMP.UNIT_ID, APP_USER_ID, APP_USER_ID || ' ' || EMP_FNAME || ' ' || EMP_LNAME EMP_NAME FROM KPDBA.COURSE_APPLICANT CA, KPDBA.COURSE_QUERY_TIME CQT, KPDBA.EMPLOYEE EMP WHERE     CA.APP_USER_ID = EMP.EMP_ID AND CA.QUERY_ID = CQT.QUERY_ID) APPLICANT, (SELECT UNIT_ID, UNIT_DESC FROM KPDBA.UNIT) UNT, KPDBA.COURSE_QUERY_ATTN CQA WHERE     APPLICANT.QUERY_ID = CQA.QUERY_ID(+) AND APPLICANT.TIME_SEQ = CQA.TIME_SEQ(+) AND APPLICANT.APP_EMP_ID = CQA.APP_EMP_ID(+) AND APPLICANT.UNIT_ID = UNT.UNIT_ID AND APPLICANT.QUERY_ID = :as_query_id AND APPLICANT.TIME_SEQ = :ai_time_seq) GROUP BY UNIT_ID, UNIT_DESC";
        public string Q_GET_APPLICANT = "SELECT APPLICANT.DOC_TYPE, APPLICANT.QUERY_ID, APPLICANT.TIME_SEQ, APPLICANT.APP_EMP_ID, EMP_NAME, NVL (TRAINING_FLAG, 'F') TRAINING_FLAG FROM (SELECT 'ISO' DOC_TYPE, ICT.QUERY_ID, ICT.TIME_SEQ, EMP.UNIT_ID, CA.APP_EMP_ID, CA.APP_EMP_ID || ' ' || EMP_FNAME || ' ' || EMP_LNAME EMP_NAME FROM KPDBA.ISO_COURSE_APPLICANT CA, KPDBA.ISO_COURSE_TIME ICT, KPDBA.EMPLOYEE EMP WHERE     CA.APP_EMP_ID = EMP.EMP_ID AND CA.QUERY_ID = ICT.QUERY_ID AND CA.QUERY_ID = :as_query_id UNION ALL SELECT 'COURSE' DOC_TYPE, CA.QUERY_ID, CQT.TIME_SEQ, EMP.UNIT_ID, APP_USER_ID, APP_USER_ID || ' ' || EMP_FNAME || ' ' || EMP_LNAME EMP_NAME FROM KPDBA.COURSE_APPLICANT CA, KPDBA.COURSE_QUERY_TIME CQT, KPDBA.EMPLOYEE EMP WHERE CA.APP_USER_ID = EMP.EMP_ID AND CA.QUERY_ID = CQT.QUERY_ID) APPLICANT, (SELECT UNIT_ID, UNIT_DESC FROM KPDBA.UNIT) UNT, KPDBA.COURSE_QUERY_ATTN CQA WHERE     APPLICANT.QUERY_ID = CQA.QUERY_ID(+) AND APPLICANT.TIME_SEQ = CQA.TIME_SEQ(+) AND APPLICANT.APP_EMP_ID = CQA.APP_EMP_ID(+) AND APPLICANT.UNIT_ID = UNT.UNIT_ID AND APPLICANT.QUERY_ID = :as_query_id AND APPLICANT.TIME_SEQ = :ai_time_seq AND APPLICANT.UNIT_ID = :unit_id";
        public string Q_CHECK_APPLICANT_ID = "SELECT APP_EMP_ID, EMP_NAME, UNIT_DESC, TRAINING_FLAG FROM(SELECT APPLICANT.DOC_TYPE, APPLICANT.QUERY_ID, APPLICANT.TIME_SEQ, APPLICANT.APP_EMP_ID, EMP_NAME, APPLICANT.UNIT_ID, UNT.UNIT_DESC, NVL (TRAINING_FLAG, 'F') TRAINING_FLAG FROM (SELECT 'ISO' DOC_TYPE, ICT.QUERY_ID, ICT.TIME_SEQ,   EMP.UNIT_ID, CA.APP_EMP_ID, CA.APP_EMP_ID || ' ' || EMP_FNAME || ' ' || EMP_LNAME EMP_NAME FROM KPDBA.ISO_COURSE_APPLICANT CA, KPDBA.ISO_COURSE_TIME ICT, KPDBA.EMPLOYEE EMP WHERE     CA.APP_EMP_ID = EMP.EMP_ID AND CA.QUERY_ID = ICT.QUERY_ID AND CA.QUERY_ID = :AS_QUERY_ID UNION ALL SELECT 'COURSE' DOC_TYPE, CA.QUERY_ID, CQT.TIME_SEQ, EMP.UNIT_ID, APP_USER_ID, APP_USER_ID || ' ' || EMP_FNAME || ' ' || EMP_LNAME EMP_NAME FROM KPDBA.COURSE_APPLICANT CA, KPDBA.COURSE_QUERY_TIME CQT, KPDBA.EMPLOYEE EMP WHERE CA.APP_USER_ID = EMP.EMP_ID AND CA.QUERY_ID = CQT.QUERY_ID) APPLICANT, (SELECT UNIT_ID, UNIT_DESC FROM KPDBA.UNIT) UNT, KPDBA.COURSE_QUERY_ATTN CQA WHERE     APPLICANT.QUERY_ID = CQA.QUERY_ID(+) AND APPLICANT.TIME_SEQ = CQA.TIME_SEQ(+) AND APPLICANT.APP_EMP_ID = CQA.APP_EMP_ID(+) AND APPLICANT.QUERY_ID = :AS_QUERY_ID AND APPLICANT.UNIT_ID = UNT.UNIT_ID AND APPLICANT.TIME_SEQ = :AI_TIME_SEQ) WHERE APP_EMP_ID = :APP_EMP_ID";
        public string queryAllcourse = "SELECT 'COURSE'  DOC_TYPE , COURSE_ID DOC_ID , '' DOC_REV , COURSE_DESC , REMARK FROM KPDBA.COURSE_MASTER WHERE ONLINE_CLASS_FLAG = 'T'";
        public string queryAllISO = "SELECT  'ISO'  DOC_TYPE , DOC_CODE , DOC_REVISION , ISO_STD || ' ' ||DOC_NAME DOC_NAME, ISO_STD FROM  KPDBA.ISO_MASTER WHERE DOC_STATUS = 'R' AND ONLINE_CLASS_FLAG = 'T'";


        public string Q_INSERT_SET_CHECK = "INSERT INTO KPDBA.COURSE_QUERY_ATTN (QUERY_ID, TIME_SEQ, APP_EMP_ID, TRAINING_FLAG, ATTN_TIME, CR_DATE, CR_ORG_ID, CR_USER_ID) VALUES (:as_query_id, TO_NUMBER (:ai_time_seq), TO_CHAR (:app_emp_id), :training_flag, SYSDATE, SYSDATE, TO_CHAR (:org), TO_CHAR (:user_id))";
    }
}