//         public string Q_GET_COURE_FORM = "SELECT COURSE_DESC FROM(SELECT 'COURSE' DOC_TYPE, CM.COURSE_ID, CM.COURSE_DESC, CQ.QUERY_ID, PLACE, CQT.TIME_SEQ, LECT_NAME, APPLICANT_COUNT, COURSE_DATE QUERY_BEGIN, NVL ( COURSE_DATE + TO_NUMBER (DAY_HOUR) / 24 + TO_NUMBER (DAY_MIN) / 24 / 60, COURSE_DATE) QUERY_END, NVL (DAY_HOUR, 0) DAY_HOUR, NVL (DAY_MIN, 0) DAY_MIN, NVL (CQ.INSTANT_FLAG, 'F') INSTANT_FLAG, CQ.CR_USER_ID FROM KPDBA.COURSE_MASTER CM, KPDBA.COURSE_QUERY CQ, KPDBA.COURSE_QUERY_TIME CQT, (  SELECT QUERY_ID, LISTAGG (THAI_FNAME || ' ' || THAI_SNAME, ', ') WITHIN GROUP (ORDER BY QUERY_ID, THAI_FNAME, THAI_SNAME) LECT_NAME FROM KPDBA.COURSE_LECTURER CL, KPDBA.LECTURER_MASTER LM WHERE CL.LECT_ID = LM.LECT_ID GROUP BY QUERY_ID) LECT, (  SELECT QUERY_ID, COUNT (APP_USER_ID) APPLICANT_COUNT FROM KPDBA.COURSE_APPLICANT CA GROUP BY QUERY_ID) APPLI WHERE     CLOSE_FLAG = 'F' AND CM.COURSE_ID = CQ.COURSE_ID AND CQ.QUERY_ID = CQT.QUERY_ID(+) AND ONLINE_CLASS_FLAG = 'T' AND CQ.QUERY_ID = LECT.QUERY_ID(+) AND CQ.QUERY_ID = APPLI.QUERY_ID(+) AND NVL (INSTANT_FLAG, 'F') LIKE :as_instant_flag AND CQ.CR_USER_ID LIKE :as_user_id UNION ALL SELECT 'ISO' DOC_TYPE, IM.DOC_CODE || '/' || IM.DOC_REVISION DOC_CODE, DOC_NAME, ICQ.QUERY_ID, PLACE, ICT.TIME_SEQ, LECT_EMP_ID || ' ' || EMP_FNAME || ' ' || EMP_LNAME EMP_NAME, APPLICANT_COUNT, ICT.COURSE_DATE QUERY_BEGIN, NVL ( ICT.COURSE_DATE + TO_NUMBER (DAY_HOUR) / 24 + TO_NUMBER (DAY_MIN) / 24 / 60, ICT.COURSE_DATE) QUERY_END, NVL (DAY_HOUR, 0) DAY_HOUR, NVL (DAY_MIN, 0) DAY_MIN, NVL (ICQ.INSTANT_FLAG, 'F') INSTANT_FLAG, ICQ.CR_USER_ID FROM KPDBA.ISO_MASTER IM, KPDBA.ISO_COURSE_QUERY ICQ, KPDBA.ISO_COURSE_TIME ICT, KPDBA.EMPLOYEE EMP, (  SELECT QUERY_ID, COUNT (APP_EMP_ID) APPLICANT_COUNT FROM KPDBA.ISO_COURSE_APPLICANT CA GROUP BY QUERY_ID) APPLI WHERE     IM.DOC_CODE = ICQ.DOC_CODE AND IM.DOC_REVISION = ICQ.DOC_REVISION AND ICQ.QUERY_ID = ICT.QUERY_ID(+) AND DOC_STATUS <> 'C' AND ACCEPT_HIST_FLAG = 'T' AND EMP.EMP_ID = LECT_EMP_ID AND IM.ONLINE_CLASS_FLAG = 'T' AND ICQ.QUERY_ID = APPLI.QUERY_ID(+) AND NVL (INSTANT_FLAG, 'F') LIKE :as_instant_flag AND ICQ.CR_USER_ID LIKE :as_user_id) GROUP BY COURSE_DESC";
