UPDATE KPDBA.COURSE_TOPIC SET TOPIC_ORDER = TO_NUMBER (:AD_TOPIC_ORDER), UPDATE_DATE = SYSDATE, CANCEL_FLAG = :AD_CANCEL_FLAG, UP_ORG_ID = TO_CHAR (:AD_ORG_ID), UP_USER_ID = TO_CHAR (:AD_USER_ID) WHERE     COURSE_ID = :AD_COURSE_ID AND COURSE_REVISION = :AD_COURSE_REVISION AND TOPIC_ID = :AD_TOPIC_ID